    public void tema(int num) {
        switch (num) {
            case 0:
                jTextArea1.setBackground(new Color(255, 255, 255));
                jTextArea1.setForeground(new Color(0, 0, 0));
                break;
            case 1:
                jTextArea1.setBackground(new Color(50, 50, 50));
                jTextArea1.setForeground(new Color(255, 255, 255));
                break;
        }

    }

